css:
/*! minireset.css v0.0.3 | MIT License | github.com/jgthms/minireset.css */html,body,p,ol,ul,li,dl,dt,dd,blockquote,figure,fieldset,legend,textarea,pre,iframe,hr,h1,h2,h3,h4,h5,h6{margin:0;padding:0}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal}ul{list-style:none}button,input,select,textarea{margin:0}html{box-sizing:border-box}*,*:before,*:after{box-sizing:inherit}img,embed,iframe,object,audio,video{height:auto;max-width:100%}iframe{border:0}table{border-collapse:collapse;border-spacing:0}td,th{padding:0;text-align:left}

/*	https://jsfiddle.net/saeokn81/	*/
body::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-image: url("./data/lagu1.jpg");
  z-index: -1;
}

body {
  padding-bottom: 2000px;
}

body		{background:#e7e7e7; font-family:monospace, sans-serif; font-size:16px; line-height:24px; color:#444; width:100%; padding:2em;}
h1			{font-size:4em; font-weight:normal; color:#1400ff;}
h2			{font-size:2em; font-weight:normal;}
h3			{font-weight:normal; color:#666; margin-bottom:1em;}
a			{color:#1400ff; word-break:break-all}
blockquote	{display:block; margin:0 auto; margin:1em; font-style:italic;}

.all		{width:960px; margin:0 auto; background-color:#fff; padding:2em;}
.all img	{width:89px;}
.show img	{width:448px;}
.start		{text-align:center; font-size:4em;}
.chan		{background-color:#fff; margin:0 auto; width:440px; padding:1em 2em;}
.cast		{background-color:#fff; margin:0 auto; line-height:1.75em; padding:1em 2em; margin-top:2em;}
.cast img	{width:initial;}
.cc			{background-color:#fff; margin:0 auto; font-size:10px; line-height:1.75em; padding:2em 0; margin-top:2em; width:640px; text-align:center; word-break:break-all}
.im			{background-color:#fff; margin:0 auto; font-size:10px; line-height:1.75em; padding:2em; margin-top:2em; width:640px; text-align:center; word-break:break-all}
.back_link	{display:block; background-color:#fff; text-align:center; margin:0 auto; width:200px; margin-top:2em; padding:0.5em 0;}
.wrap		{display:flex; margin:0 auto; flex-wrap:wrap; justify-content:space-evenly; gap:14px; margin-top:15px;}
.chan1,
.chan2,
.chan3,
.chan4		{flex-basis:260px; padding:20px 20px;}


@media (min-width: 360px) and (max-width: 767.98px) {
html        {font-size:18px;}
body        {font-size:1.0rem; line-height:1.75em;}
.all		{width:800px;}
.all img	{width:72px;}
.cc,
.im			{width:initial; padding:2em;}
.show img,
.cast img	{width:initial;}

}

@media (min-width: 768px) and (max-width: 991.98px) {
html        {font-size:18px;}
body        {font-size:1.0rem; line-height:1.75em;}
.all		{width:800px;}
.all img	{width:72px;}
.cc,
.im			{width:initial; padding:2em;}
.show img,
.cast img	{width:initial;}

}
